<?phpfunction msr_elearn_results_create_db() {	global $wpdb;	$charset_collate = $wpdb->get_charset_collate();	$table_name = $wpdb->prefix . 'msr_elearn_results';	$sql = "CREATE TABLE $table_name (			result_id mediumint(9) NOT NULL AUTO_INCREMENT,			diagonisma_id int(11) NOT NULL,			first_name VARCHAR(50) NOT NULL,			second_name VARCHAR(50) NOT NULL,			am VARCHAR(10) NOT NULL,			etos_spoudon VARCHAR(20) NOT NULL,			tmima VARCHAR(100) NOT NULL,			time_submitted datetime DEFAULT '0000-00-00 00:00:00' NOT NULL,			status VARCHAR(100) NOT NULL,			answers TEXT NOT NULL,			vathmologia VARCHAR(10) NOT NULL,			PRIMARY KEY (result_id)		) $charset_collate;";	require_once( ABSPATH . 'wp-admin/includes/upgrade.php' );	dbDelta( $sql );}